<?php require_once __DIR__ . '/config.php'; class DatabaseConfig { public static function initialize() { try { $db = new PDO('sqlite:' . DB_PATH); $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION); $schema = file_get_contents(__DIR__ . '/../database/schema.sql'); $db->exec($schema); return $db; } catch (Exception $e) { error_log('Database initialization failed: ' . $e->getMessage()); throw $e; } } }